INSERT INTO `iwebshop_goods` VALUES( '70','川贝止嗽散','SD139486098452','0','24.50','28.50','0.00','2014-03-15 13:27:55','','2014-03-15 13:27:55','100','upload/2014/03/15/20140315012638639.jpg','0','<table border=\"0\" style=\"font-family:tahoma, arial, \'宋体\', sans-serif;font-size:14px;background-color:#FFFFFF;height:175px;color:#404040;width:602px;\" class=\"ke-zeroborder\"><tbody><tr><td>\n				<p>\n					<span style=\"color:#000000;font-size:14px;\"><strong><span style=\"color:#000000;font-size:14px;\">川貝止嗽散能止咳化痰、生津潤肺，協助修復呼吸道的黏膜組織。在感冒中後期，痰多的情況會造成鼻涕倒流、留滯胃中。川貝止嗽散可幫助清除胃中痰液，達致止咳化痰的功效。</span></strong></span>\n				</p>\n				<p>\n					<span style=\"color:#000000;\"></span>\n				</p>\n			</td>\n		</tr></tbody></table><table cellspacing=\"0\" cellpadding=\"0\" width=\"90%\" border=\"0\" style=\"font-family:tahoma, arial, \'宋体\', sans-serif;font-size:14px;background-color:#FFFFFF;color:#404040;\" class=\"ke-zeroborder\"><tbody><tr><td valign=\"top\" align=\"left\">\n				<span style=\"color:#000000;\"><strong><span></span></strong></span>\n			</td>\n			<td valign=\"top\" align=\"left\">\n				<span style=\"color:#000000;font-size:14px;\"><strong><span style=\"color:#000000;font-size:14px;\">功效</span></strong></span>\n			</td>\n		</tr><tr><td valign=\"top\" align=\"left\">\n				<span><span><span><span style=\"color:#000000;\"></span></span></span></span><br /></td>\n			<td valign=\"top\" align=\"left\">\n				<span style=\"color:#000000;font-size:14px;\"><span style=\"color:#000000;font-size:14px;\"><span style=\"color:#000000;font-size:14px;\"><span style=\"color:#000000;font-size:14px;\"><strong>化痰止咳</strong></span></span></span></span><br /></td>\n		</tr><tr><td valign=\"top\" align=\"left\">\n				<span><span><span><span style=\"color:#000000;\"></span></span></span></span><br /></td>\n			<td valign=\"top\" align=\"left\">\n				<span style=\"color:#000000;font-size:14px;\"><span style=\"color:#000000;font-size:14px;\"><span style=\"color:#000000;font-size:14px;\"><span style=\"color:#000000;font-size:14px;\"><strong>止咳平喘，滋潤心肺</strong></span></span></span></span><br /></td>\n		</tr><tr><td valign=\"top\" align=\"left\">\n				<span><span><span><span style=\"color:#000000;\"></span></span></span></span><br /></td>\n			<td valign=\"top\" align=\"left\">\n				<span style=\"color:#000000;font-size:14px;\"><strong><span style=\"color:#000000;font-size:14px;\">健脾滲濕，袪濕袪痰</span></strong></span>\n			</td>\n		</tr><tr><td valign=\"top\" align=\"left\">\n				<span style=\"color:#000000;\"><strong><span></span></strong></span>\n			</td>\n			<td valign=\"top\" align=\"left\">\n				<span style=\"color:#000000;font-size:14px;\"><strong><span style=\"color:#000000;font-size:14px;\">清熱瀉肝</span></strong></span>\n				<p>\n					<span style=\"color:#000000;\"><strong><span><span style=\"color:#000000;font-size:14px;\">中醫認為感冒是由風邪病毒入侵人體，導致患者出現發熱、頭痛、鼻塞、流涕、噴嚏、惡風等症狀的一種常見外感病。 簡括而言, 可按病癥分為以下四個</span><br /><span style=\"color:#000000;font-size:14px;\">感冒分期：</span></span></strong></span>\n				</p>\n				<ul><li style=\"text-align:left;\">\n						<span style=\"color:#000000;font-size:14px;\"><strong><span style=\"color:#000000;font-size:14px;\">感冒初期：症狀包括噴嚏、鼻塞、鼻流清涕、咽喉痕癢、鼻咽不適、身體發冷、輕度惡寒或惡風。這個階段有時稱為「傷風」，症狀一般較為輕微。</span></strong></span>\n					</li>\n					<li style=\"text-align:left;\">\n						<span style=\"color:#000000;font-size:14px;\"><strong><span style=\"color:#000000;font-size:14px;\">感冒中期：症狀包括發熱、咽痛、頭痛，全身關節或肌肉疼痛；輕度咳嗽、開始有白痰，上呼吸道炎症出現。</span></strong></span>\n					</li>\n					<li style=\"text-align:left;\">\n						<span style=\"color:#000000;font-size:14px;\"><strong><span style=\"color:#000000;font-size:14px;\">感冒中/後期：嚴重咳嗽、痰多帶黃、氣喘。</span></strong></span>\n					</li>\n					<li style=\"text-align:left;\">\n						<span style=\"color:#000000;font-size:14px;\"><strong><span style=\"color:#000000;font-size:14px;\">感冒復原期：食慾不振、噁心、口苦、疲勞等症狀。</span></strong></span>\n					</li>\n				</ul></td>\n		</tr></tbody></table>','','','川贝止嗽散','0.00','0','千克','0','1','0','99','','0','0','0','0' ); 
