 ) ENGINE=MyISAM CHARSET=utf8 COMMENT='属性值表' AUTO_INCREMENT=94;

DROP TABLE IF EXISTS `iwebshop_goods_car`;
 CREATE TABLE `iwebshop_goods_car` (
