 ) ENGINE=MyISAM CHARSET=utf8 COMMENT='记录某件商品对于某组会员的价格关系表，优先权大于组设定的折扣率。' AUTO_INCREMENT=1;

DROP TABLE IF EXISTS `iwebshop_guide`;
